var ViewJs=(function(options){var nl=document.getElementsByClassName('slide');var play_btn=document.getElementById('play-btn');var slides=[];for(var i=0,ref=slides.length=nl.length;i<ref;i++){slides[i]=nl[i]};var slides_count=slides.length;var current_slide=slides[0];document.addEventListener('keydown',move,false);var el=function(id){return document.getElementById(id)};var next_slide=function(){var current_slide_index=slides.indexOf(current_slide);if(current_slide_index==slides_count-1)return slides[current_slide_index];else{slides[current_slide_index+1].scrollIntoView(true);return slides[current_slide_index+1]}};var prev_slide=function(){var current_slide_index=slides.indexOf(current_slide);if(current_slide_index==0)return slides[current_slide_index];else{slides[current_slide_index-1].scrollIntoView(true);return slides[current_slide_index-1]}};function move(e){switch(e.keyCode){case 40:return current_slide=next_slide();case 38:return current_slide=prev_slide()}};function presentation_start(){current_slide=next_slide()}function play_presentation(slide_show_timeout){setInterval(presentation_start,slide_show_timeout)}check_slide_show(options);function check_slide_show(options){if(options.slide_show==true&&options.slide_show_timeout>0)play_presentation(options.slide_show_timeout)}});var options={slide_show:true,slide_show_timeout:10000};ViewJs(options);